array = input.split("")
floor_number = 0
step_count = 1
step_basement = 0
in_basement = false
array.each do |item|
  #Part 1
  item == "(" ? floor_number += 1 : floor_number -=1

  #Part 2
  if floor_number < 0 && !in_basement
    in_basement = true
    step_basement = step_count
  end
  step_count+=1
end
puts "Your answer is #{floor_number}"
puts "You enter the basement at #{step_basement}"
