class House {
	constructor(x, y) {
		this._x = x;
		this._y = y;
		this.visits = 1;
	}

	addVisit() {
		this.visits++;
	}

	getX() {
		return this._x;
	}

	getY() {
		return this._y;
	}
}

class HouseMap {
	constructor() {
		this._houseMap = new Array();
		this._totalHouses = 0;
	}

	addHouse(x, y) {
		const house = new House(x, y);
		this._houseMap.push(house);
		this._totalHouses++;
	}

	findHouse(x, y) {
		return this._houseMap.findIndex(
			(house) => house.getX() === x && house.getY() === y
		);
	}

	placeHouse(x, y) {
		const houseIndex = this.findHouse(x, y);
		if (houseIndex >= 0) {
			this._houseMap[houseIndex].addVisit();
		} else {
			this.addHouse(x, y);
		}
	}

	getTotalHouses() {
		return this._totalHouses;
	}
}

