function directionSense(location, direction, houseMap) {
	if (direction === "^") {
		location.x += 1;
		houseMap.placeHouse(location.x, location.y);
	} else if (direction === "v") {
		location.x -= 1;
		houseMap.placeHouse(location.x, location.y);
	} else if (direction === ">") {
		location.y += 1;
		houseMap.placeHouse(location.x, location.y);
	} else if (direction == "<") {
		location.y -= 1;
		houseMap.placeHouse(location.x, location.y);
	}
}

const houseMap = new HouseMap();
houseMap.addHouse(0, 0);
const inputArray = input.split("");
let location = { x: 0, y: 0 };

// Santa alone
for (const direction of inputArray) {
	directionSense(location, direction, houseMap);
}

console.log(houseMap.getTotalHouses());

const secondHouseMap = new HouseMap();

// Santa and robot
let santaLocation = { x: 0, y: 0 };
let robotLocation = { x: 0, y: 0 };

/** Start Santa */
secondHouseMap.addHouse(0, 0);
directionSense(santaLocation, inputArray[0], secondHouseMap);

for (let i = 1; i < inputArray.length; i++) {
	if (i % 2 === 0) {
		/* Santa Moves */
		directionSense(santaLocation, inputArray[i], secondHouseMap);
	} else {
		/**Robot Moves */
		directionSense(robotLocation, inputArray[i], secondHouseMap);
	}
}

console.log(secondHouseMap.getTotalHouses());
